const DATA_MAP = {
  spelling: SPELLING_DATA,
  spacing: SPACING_DATA,
  idiom: IDIOM_DATA,
  proverb: PROVERB_DATA,
  synonym: SYNONYM_DATA,
  antonym: ANTONYM_DATA,
  honorific: HONORIFIC_DATA,
  it_term: IT_TERM_DATA,
  it_spell: IT_SPELL_DATA,
};

const allCategoryKeys = Object.keys(DATA_MAP);

export function generateKoreanQuestions(category, count) {
  let pool;
  if (category === "random") {
    pool = [];
    for (const key of allCategoryKeys) {
      pool.push(...DATA_MAP[key].map(item => ({ ...item, _cat: key })));
    }
  } else {
    pool = DATA_MAP[category].map(item => ({ ...item, _cat: category }));
  }

  const shuffled = shuffle(pool);
  const selected = shuffled.slice(0, Math.min(count, shuffled.length));

  return selected.map(item => {
    const choices = shuffle([
      { label: item.correct, isCorrect: true },
      ...item.wrong.map(w => ({ label: w, isCorrect: false })),
    ]);
    return {
      question: item.question,
      answer: item.correct,
      choices,
      hint: item.hint,
      category: category === "random" ? item._cat : category,
    };
  });
}

